import React, { useState } from 'react'

import firebase from '../../firebase.js';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'

const Join = () => {
    const [youName, setYouName] = useState("");
    const [youEmail, setYouEmail] = useState("");
    const [youPass, setYouPass] = useState("");
    const [youPassC, setYouPassC] = useState("");

    let navigate = useNavigate();

    const JoinFunction = (e) => {
        e.preventDefault();
    }

  return (
    <div id='login__wrap'>
        <div className='login__bg'>
            <div className='login__bg__text'>join</div>
        </div>
        <form action="" className='login__form'>
            <fieldset>
                <legend className='blind'>회원가입 영역</legend>
                <div className='login__id'>
                    <label htmlFor="youName" className='blind'>이름</label>
                    <input className='youName' type="text" name='youName' placeholder='이름' autoComplete='off' required/>
                </div>
                <div className='login__id'>
                    <label htmlFor="youEmail" className='blind'>이메일</label>
                    <input className='youEmail' type="email" name='youEmail' placeholder='이메일' autoComplete='off' required/>
                </div>
                <div className='login__pass'>
                    <label htmlFor="youPass" className='blind'>비밀번호</label>
                    <input className='youPass' type="text" name='youPass' placeholder='비밀번호' autoComplete='off' required/>
                </div>
                <div className='login__passC'>
                    <label htmlFor="youPassC" className='blind'>비밀번호확인</label>
                    <input className='youPassC' type="text" name='youPassC' placeholder='비밀번호 확인' autoComplete='off' required/>
                </div>
                <button className='login__btn' type='submit' onClick={(e) => JoinFunction(e)}>회원가입</button>
            </fieldset>
        </form>
    </div>
  )
}

export default Join
