import React, { useState } from 'react'
import { useNavigate } from "react-router-dom";

import axios from 'axios';

const Write = () => {

    const [title, setTitle] = useState("");
    const [content, setContent] = useState("");
    let navigate = useNavigate();

    const onSubmit = (e) => {
        e.preventDefault();

        if (title === "" || content === "") {
            return alert("Ï†úÎ™© ÎòêÎäî ÎÇ¥Ïö©ÏùÑ Ï±ÑÏ£ºÏöî!");
        }

        let body = {
            title: title,
            content: content,
        }

        axios
            .post("/api/post/write", body)
            .then((resopnse) => {
                if (resopnse.data.success) {
                    alert("Í∏Ä ÏûëÏÑ±Ïù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.");
                    navigate("/list");
                } else {
                    alert("Í∏Ä ÏûëÏÑ±Ïù¥ Ïã§Ìå®ÌïòÏòÄÏäµÎãàÎã§.")
                }
            })
    }

    return (
        <div className='login__wrap'>
            <div className="login__header">
                <h3>Write</h3>
                <p>Í∏Ä ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöîü§ü</p>
            </div>
            <form className='login__form'>
                <fieldset>
                    <legend className="blind">Î°úÍ∑∏Ïù∏ ÏòÅÏó≠</legend>
                    <div>
                        <label htmlFor='title' className="required blind">Í∏ÄÏ†úÎ™©</label>
                        <input
                            type="text"
                            id="youName"
                            placeholder='Í∏ÄÏ†úÎ™©ÏùÑ ÏûëÏÑ±Ìï¥Ï£ºÏÑ∏Ïöî'
                            value={title || ""}
                            onChange={(event) => {
                                setTitle(event.currentTarget.value);
                            }}
                        />
                    </div>
                    <div>
                        <label htmlFor="youName" className="required blind">Í∏ÄÎÇ¥Ïö©</label>
                        <textarea
                            type="text"
                            id="content"
                            placeholder='Í∏Ä ÎÇ¥Ïö©ÏùÑ Ï†ÅÏñ¥Ï£ºÏÑ∏Ïöî'
                            value={content || ""}
                            onChange={(event) => {
                                setContent(event.currentTarget.value);
                            }}
                        />

                    </div>
                    <div>
                        <button
                            type="submit"
                            className="btn"
                            onClick={(e) => {
                                onSubmit(e);
                            }}
                        >Í∏ÄÏì∞Í∏∞</button>
                    </div>
                </fieldset>
            </form>
        </div>
    )
}

export default Write
