import axios from 'axios';
import React, { useEffect, useState } from 'react'

const List = () => {
  const [postList, setPostList] = useState([]);

  useEffect(() => {
    axios.post("/api/post/list")
    .then((response) => {
      if(response.data.success){
        setPostList([...response.data.postList])
      }
    })
    .catch((err) => {
      console.log(err)
    })
  },[postList])
  return (
    <>
      <div className='login__bg'>
          <div className='login__bg__text'>List</div>
      </div>
      <div className='list__wrap'>
        <div className='list'>
          <span className='cate'>교육</span>
          <h3 className='title'>마케팅 초기 이메일 작성기</h3>
          <p className='desc'>귀찮은 파워포인트 제작을 하나의 프롬프트로 끝낼 수 있습니다. 한 번에, 슬라이드 내용, 스크립트까지 작성합니다.</p>
          <div className='author'>작가</div>
        </div>
      </div>
    </>
  )
}

export default List
